(()=>{const e=new IntersectionObserver((function(e,t){e.forEach((e=>{e.isIntersecting&&(e.target.classList.add("is-animated"),t.unobserve(e.target))}))}),{root:null,rootMargin:"0px",threshold:.5});document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll(".do-anim").forEach((t=>{e.observe(t)}))}))})();